<style scoped>
.login-content {
    align-items: center;
    justify-content: center;
    margin-top: auto;
    margin-bottom: auto;
}

.login-wrapper {
    display: flex;
    flex-direction: column;
}

.text-da {
    color: #A7BBC8;
}
</style>
<script>
import {mapActions} from 'vuex'
import {ref} from 'vue'
import router from "../router";
import useVuelidate from '@vuelidate/core'
import {required, email} from '@vuelidate/validators'
import {notification} from 'ant-design-vue';

export default {
    name: 'register',
    setup() {
        return {v$: useVuelidate()}
    },
    data() {
        return {
            user: {
                name: "",
                email: "",
                role: "",
                password: "",
                password_confirmation: "",
                newsletter: false
            },
            processing: false,
            errors: {email: []},
            email_editable: false,
            token: "",

        }
    },
    validations() {
        return {
            user: {
                name: {required},
                email: {required, email},
                role: {required},
                password: {required},
                password_confirmation: {},
                newsletter: {}
            }

        }
    },
    mounted() {
        setTimeout(() => {
            let t = this.$route.query.token;
            this.token = this.$route.query.token;
            console.log('call t',t)
            if (t) {
                console.log('call verify')
                this.verifyToken()
            } else {
                this.user.email = "";
                this.email_editable = false;
                this.$store.state.community.invite = null;
            }
        }, 1000);

    },
    methods: {
        ...mapActions({
            signIn: 'auth/login'
        }),

        verifyToken() {
            this.$store.dispatch('member/verifyToken', this.token).then(({data}) => {
                if (data !== "Expired") {
                    console.log('token',this.token);
                    this.user.email = data.email
                    this.email_editable = true;
                    this.$store.state.community.invite = data;
                } else {
                    notification['error']({
                        message: "Link Expired."
                    });
                    setTimeout(() => {
                        this.$router.push('register');
                    }, 3000);
                }
            });
        },

        async register() {
            this.v$.$validate()
            this.v$.$touch()
            this.errors = {email: []}
            this.v$.user.email.$errors.push({});
            if (this.v$.$invalid) {
                return
            }

            this.user.password_confirmation = this.user.password;
            this.user.newsletter = this.user.newsletter === true ? 1 : 0;
            console.log(this.user)
            // return
            this.processing = true
            await axios.post('/register', this.user).then(response => {
                this.signIn()
                console.log('response------',response)
                if (this.$store.state.community.invite != null) {
                    this.$store.dispatch('community/joinCommunity', this.$store.state.community.invite.community_id)
                    this.$store.dispatch('community/acceptInvitation', this.$store.state.community.invite)
                    router.push({name: 'community_browse'})

                } else {
                    router.push('onboarding-01')

                }
            }).catch(({response: {data}}) => {
                // notification['error']({
                //     message: data.message
                // });
                console.log(data)
                this.errors = data.errors
                // alert(data)
            }).finally(() => {
                this.processing = false
            })
        },


    }
}
</script>

